mapper.select:
SELECT id, goods_id, seckill_price, start_date, end_date, updated_date, created_date
FROM `seckill.seckill_goods`
WHERE is_deleted = false and goods_id = #{goodsId}

SELECT id, goods_id, seckill_price, start_date, end_date, updated_date, created_date
FROM `seckill.seckill_goods`
WHERE is_deleted = 0


        AND id=#{id}


        AND goods_id=#{goodsId}

mapper.update:
